// <-- USING-FORM-HOOK-START -->
import * as React from "react";
import { useForm } from "react-hook-form";
import Heading from "./Heading";

export default function App() {
  const { register, handleSubmit } = useForm();

  const onSubmit = (data) => {
    alert(JSON.stringify(data))
    console.log(data)
  };

//   const handleClick = () => {
//     fetch("http://localhost:9292/patients/"+ patient.id, {
//         method: "DELETE",
//         headers: {
//             'Content-Type': 'application/json',
//         } 
//     })
//     updateCourses(course.id)
// }

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <div style={{ marginBottom: 30 }}>
        <h1 className="h1">
          Patient Admission Form
        </h1>
        <p style={{ fontSize: 14, lineHeight: 1.3 }}>
          Fill out the form to get added as a patient.
        </p>
      </div>
      <input {...register("firstName")} placeholder="First name" />
      <input {...register("lastName")} placeholder="Last name" />
      <select {...register("category")}>
        <option value="">Reason for visit...</option>
        <option value="Covid">Covid</option>
        <option value="General">General</option>
        <option value="Neurological">Neurological</option>
        <option value="Respitory">Respitory</option>
        <option value="Cardiovascular">Cardiovascular</option>
      </select>
      <input type="submit" />
    </form>
  );
}
// <-- USING-FORM-HOOK-END -->


